select * from employees;

-- retrieve the emp_id, emp_name, and dept_id from the employees table, where the location is 'Cairo'--select emp_id, emp_name , dept_id from employees where location = 'cairo';--display distinct dept_id values from the employees tables--select distinct dept_id from employees;--Write a SQL query to create a table students with the following columns: ID(Primary Key), First_Name (not null), Last_Name (default 'Unknown'), Address (default 'N/A'), City (default 'N/A'), and Birth_Date--create table Students(ID int primary key,First_name varchar(20) not null,last_name varchar(20) default 'unknown',Address varchar(20) default 'N/A',city varchar(20) default 'N/A',birth_date date);-- Write a SQL query to drop the students table--drop table Students;--Write a SQL query to insert the following values into the students table: ('Ahmed', 'Ali', 'Downtown', 'Cairo', '1995-01-01')--insert into Students(first_name,last_name,Address,city,birth_date)values('Ahmed', 'Ali', 'Downtown', 'Cairo', '1995-01-01');-- update the Address of the student with Last_Name = 'Ahmed' to 'Garden City--update Students set Address = 'Garden City'where last_name = 'ahmed';--Write a SQL query to delete the rows from the students table where City is 'Cairo', and then rollback the transactiondelete from Studentswhere city = 'Cairo';rollback;